def smooth_transition(num_steps):
    if not isinstance(num_steps, int) or num_steps <= 0:
        raise ValueError("Number of steps must be a positive integer.")

    def ease_in_out(t):
        if t < 0.5:
            return 2 * t * t  # Slow start
        else:
            return -1 + (4 - 2 * t) * t  # Gradual slowdown

    transition_values = []

    for i in range(num_steps):
        t = i / (num_steps - 1)  # Normalize to [0, 1]
        transition_values.append(ease_in_out(t))

    return transition_values

