from PIL import Image
import os
import json

def load_images(image_dir):
    images = []
    for filename in os.listdir(image_dir):
        if filename.endswith(('.png', '.jpg', '.jpeg')):
            img_path = os.path.join(image_dir, filename)
            images.append(Image.open(img_path))
    return images

def create_composite(background, layers):
    for layer in layers:
        background.paste(layer, (0, 0), layer)
    return background

def process_json_files(image_dir, json_files):
    layers = []
    for json_file in json_files:
        with open(os.path.join(image_dir, json_file), 'r') as f:
            data = json.load(f)
            for item in data['elements']:
                img = Image.open(os.path.join(image_dir, item['file']))
                layers.append(img)
    return layers

def main():
    image_dir = 'path/to/image/directory'
    background = Image.new('RGBA', (800, 600), (255, 255, 255, 255))

    natural_elements_file_path = 'natural_elements.json'
    artificial_element_files = ['frozen_lake.json', 'deer_and_rabbits.json', 'cabin.json']

    natural_layers = process_json_files(image_dir, [natural_elements_file_path])
    artificial_layers = process_json_files(image_dir, artificial_element_files)

    all_layers = natural_layers + artificial_layers
    composite_image = create_composite(background, all_layers)
    composite_image.save('winter_forest_composite.png')

