import re

def analyze_rhythm_and_meter(sonnet):
    lines = sonnet.splitlines()

    def check_iambic_pentameter(line):
        syllable_count = 0
        pattern = r'([^aeiou]*[aeiou][^aeiou]*){5}'  # Basic pattern for iambic pentameter

        for word in line.split():
            syllable_count += len(re.findall(r'[aeiou]', word.lower()))

        return syllable_count == 10 and bool(re.match(pattern, line.strip().lower()))

    deviations = []

    for i, line in enumerate(lines):
        if not check_iambic_pentameter(line):
            deviations.append((i + 1, line))

    return deviations

def compare_themes(sonnet):
    themes = {
        'love': 'central theme in many Renaissance poems',
        'time': 'often addressed in the context of fleeting beauty',
        'nature': 'used to represent human emotions'
    }

    found_themes = []
    for theme in themes.keys():
        if theme in sonnet.lower():
            found_themes.append(theme)

    return found_themes

# Example usage
