def prioritize_tasks(tasks):
    # Define a dictionary to map MoSCoW status to numeric values
    mscow_values = {
        "Must Haves": {"priority": 1},
        "Should Haves": {"priority": 2},
        "Could Haves": {"priority": 3},
        "Won't Haves": {"priority": 4}
    }

    categorized_tasks = []
    for category, details in mscow_values.items():
        for task in tasks:
            if task['category'] == category:
                categorized_tasks.append({
                    **task,
                    **details
                })

    # Sort tasks within each category based on priority
    for i in range(len(categorized_tasks)):
        for j in range(i + 1, len(categorized_tasks)):
            if categorized_tasks[i]['priority'] > categorized_tasks[j]['priority']:
                categorized_tasks[i], categorized_tasks[j] = categorized_tasks[j], categorized_tasks[i]

    return categorized_tasks

